(173, 1, '2017-07-15 12:39:52', '2017-07-15 12:39:52', '[vc_row type=\"full_width_background\" full_screen_row_position=\"middle\" scene_position=\"center\" text_color=\"dark\" text_align=\"left\" top_padding=\"100\" bottom_padding=\"100\" overlay_strength=\"0.3\"][vc_column column_padding=\"no-extra-padding\" column_padding_position=\"all\" background_color_opacity=\"1\" background_hover_color_opacity=\"1\" width=\"1/1\" tablet_text_alignment=\"default\" phone_text_alignment=\"default\"][vc_row_inner text_align=\"left\"][vc_column_inner column_padding=\"padding-3-percent\" column_padding_position=\"all\" background_color=\"#ffffff\" background_color_opacity=\"1\" centered_text=\"true\" width=\"5/12\"][/vc_column_inner][vc_column_inner column_padding=\"no-extra-padding\" column_padding_position=\"all\" background_color_opacity=\"1\" width=\"7/12\"][vc_column_text]\r\n<h1 class=\"p1\" style=\"color: #1e9af4; text-align: left;\">A data-driven approach to your visit program</h1>\r\n[/vc_column_text][nectar_btn size=\"large\" button_style=\"regular\" button_color_2=\"Extra-Color-1\" icon_family=\"none\" text=\"TRY OUR FREE DEMO\" margin_top=\"40\"][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row type=\"full_width_background\" full_screen_row_position=\"middle\" bg_color=\"#ffffff\" scene_position=\"center\" text_color=\"dark\" text_align=\"left\" top_padding=\"100\" bottom_padding=\"100\" overlay_strength=\"0.3\"][vc_column column_padding=\"no-extra-padding\" column_padding_position=\"all\" background_color_opacity=\"1\" background_hover_color_opacity=\"1\" width=\"1/6\" tablet_text_alignment=\"default\" phone_text_alignment=\"default\"][/vc_column][vc_column column_padding=\"no-extra-padding\" column_padding_position=\"all\" background_color_opacity=\"1\" background_hover_color_opacity=\"1\" width=\"2/3\" tablet_text_alignment=\"default\" phone_text_alignment=\"default\"][vc_column_text]\r\n<h2 class=\"p1\" style=\"color: #1e9af4; text-align: center;\">Wha